<span class=\"hljs-comment\">//响应后</span>\n                info.setResponse(response);\n                info.setResponseTime(System.currentTimeMillis());\n            } <span class=\"hljs-keyword\">catch</span> (Throwable e) {\n                info.setException(e);\n                info.setAction(<span class=\"hljs-string\">\"异常\"</span>);\n                <span class=\"hljs-keyword\">throw</span> e;\n            } <span class=\"hljs-keyword\">finally</span> {\n                <span class=\"hljs-comment\">//触发监听（做业务处理）</span>\n                listeners.forEach(listener -&gt; listener.onLogger(info));\n            }\n            <span class=\"hljs-keyword\">return</span> response;\n        });\n    }\n\n    <span class=\"hljs-function\"><span class=\"hljs-keyword\">protected</span> AccessLoggerInfo <span class=\"hljs-title\">createLogger</span><span class=\"hljs-params\">(MethodInterceptorHolder holder)</span> </span>{\n        AccessLoggerInfo info = <span class=\"hljs-keyword\">new</span> AccessLoggerInfo();\n\n        info.setRequestTime(System.currentTimeMillis());\n        LoggerDefine define = loggerParsers.stream()\n                .filter(parser -&gt; parser.support(ClassUtils.getUserClass(holder.getTarget()), holder.getMethod()))\n                .findAny()\n                .map(parser -&gt; parser.parse(holder))\n                .orElse(<span class=\"hljs-keyword\">null</span>);\n        <span class=\"hljs-keyword\">if</span> (define != <span class=\"hljs-keyword\">null</span>) {\n            info.setModule(define.getModule());\n            info.setDescribe(define.getDescribe());\n            Integer type = define.getType();\n            SysLogType sysLogType = SysLogType.getSysLogType(type);\n            <span class=\"hljs-keyword\">switch</span> (sysLogType){\n                <span class=\"hljs-keyword\">case</span> LOGIN:\n                    info.setAction(<span class=\"hljs-string\">\"登录\"</span>);\n                    info.setActionType(SysLogType.LOG